import React from 'react';
import logo from './logo.svg';
import './App.css';
import { Map, Marker, Popup, TileLayer } from "react-leaflet";
import { Icon } from "leaflet";

class Forecast extends React.Component {
  constructor(props) {
      super(props);
  }

  render() {
    return (
      <div class="forecast">
        <table style="width:100%" id="forecastTable">
        <tr>
          <th>Day</th>
          <th>Temperature</th>
          <th>Forecast</th>
        </tr>
        </table>
      </div>
    );
  }
}

class MapContainer extends React.Component {
    constructor(props) {
        super(props);
        this.state = {"marker": null};
    }

    // set marker on map click
    handleClick(e) {
        if (this.state.marker != null) {
            this.state.marker.remove();
        }

        this.setState({"marker": null});
        marker = L.marker(e.latlng).addTo(mymap);

        setWeatherForecast(e.latlng.lat, e.latlng.lng);
    }

    render() {
        return (
            <Map center={[34, -108]} zoom={6} onClick>
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                />
            </Map>
        );
    }

}

function App() {
  return (
    <div className="App">
      <MapContainer/>
    </div>
  );
}

export default App;

function f_to_c(t) {
    return (t - 32) * (5/9);
}

function c_to_f(t) {
    return (t * (9/5)) + 32;
}

// uses C degrees and percent
function temp_hum_to_dew(temp, humidity) {
    return (Math.pow((humidity/100), 0.125) * (112 + (0.9 * temp)))
        + (0.1 * temp)
        - 112;
}
