import './App.css';
import React, { Component } from 'react';
import { Map, TileLayer, Marker } from  "react-leaflet";
import CanvasJSReact from './canvasjs.react';
const CanvasJS = CanvasJSReact.CanvasJS;
const CanvasJSChart = CanvasJSReact.CanvasJSChart;



function Forecast(props) {
    const days = props.forecast.map(day => (
        <tr key={day['observation_time'].value}>
            <td>{day['observation_time'].value}</td>
            <td>{((day['temp'][0]['min'].value + day['temp'][1]['max'].value) / 2).toFixed(1)}</td>
            <td>{day['weather_code'].value}</td>
        </tr>
    ))

    return (
      <div>
        <table id="forecastTable">
            <thead>
                <tr>
                  <th>Day</th>
                  <th>Temperature</th>
                  <th>Forecast</th>
                </tr>
            </thead>
            <tbody>{days}</tbody>
        </table>
      </div>
    );
}

function MapContainer (props) {
    const marker = props.marker.hasLocation ? (
        <Marker position={props.marker.latlng}/>
    ) : null

    return (
        <Map
            center={[34,-108]}
            zoom={6}
            onClick={props.onClick}
            className='leaflet-container'
        >
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            />
            {marker}
        </Map>
    );
}

class App extends Component{
    constructor(props) {
        super(props);
        this.state = {
            'marker': {
                'hasLocation': false,
                'latlng': {
                    'lat': 34,
                    'lng': -108,
                }
            },
            'forecast': [],
            'options': {
                animationEnabled: true,
                exportEnabled: true,
                theme: "light2", // "light1", "dark1", "dark2"
                title:{
                    text: "Daily Temperature"
                },
                axisY: {
                    title: "Temperature",
                    includeZero: false,
                    suffix: "°F"
                },
                axisX: {
                    title: "Date",
                    interval: 2
                },
                data: [{
                    type: "line",
                    toolTipContent: "Day {x}: {y}%",
                    dataPoints: [
                        { x: 1, y: 64 },
                        { x: 2, y: 61 },
                        { x: 3, y: 64 },
                        { x: 4, y: 62 },
                        { x: 5, y: 64 },
                        { x: 6, y: 60 },
                        { x: 7, y: 58 },
                        { x: 8, y: 59 },
                        { x: 9, y: 53 },
                        { x: 10, y: 54 },
                        { x: 11, y: 61 },
                        { x: 12, y: 60 },
                        { x: 13, y: 55 },
                        { x: 14, y: 60 },
                        { x: 15, y: 56 },
                        { x: 16, y: 60 },
                        { x: 17, y: 59.5 },
                        { x: 18, y: 63 },
                        { x: 19, y: 58 },
                        { x: 20, y: 54 },
                        { x: 21, y: 59 },
                        { x: 22, y: 64 },
                        { x: 23, y: 59 }
                    ]
                }]
            }
        };

    }

    handleClick = (e) => {
        this.setState({'marker': {
                hasLocation: true,
                latlng: e.latlng
            }
        });

        // get weather forecast
        fetch("https://api.climacell.co/v3/weather/forecast/daily?lat=" + e.latlng.lat + "&lon=" + e.latlng.lng + "&unit_system=us&start_time=now&fields=precipitation%2Cprecipitation_accumulation%2Ctemp%2Cwind_speed%2Cbaro_pressure%2Cvisibility%2Chumidity%2Cweather_code&apikey=9HaH9EKcMl4ANqi3eBna6kH58fybWmTu")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({'forecast': result});
                })
    }


    render() {
        return (
            <MapContainer
                onClick={e => this.handleClick(e)}
                marker={this.state.marker}
            />
        );
    }
}
