import './App.css';
import React, { Component } from 'react'
import { Map, TileLayer, Marker } from  "react-leaflet";

class Forecast extends Component {
  constructor(props) {
      super(props);
      this.state = {
          days: []
      }
  }
/*
  updateTable() {


        date.innerHTML = day['observation_time'].value;
        temp.innerHTML = (day['temp'][0]['min'].value + day['temp'][1]['max'].value) / 2;
        forecast.innerHTML = day['weather_code'].value;
    }
    */

  render() {
    return (
      <div>
        <table id="forecastTable">
        <tr>
          <th>Day</th>
          <th>Temperature</th>
          <th>Forecast</th>
        </tr>
        </table>
      </div>
    );
  }
}

class MapContainer extends Component {
    constructor(props) {
        super(props);
        this.state = {
            'hasLocation': false,
            'latlng': {
                'lat': 34,
                'lng': -108,
            }
        };
    }

    render() {
        const marker = this.state.hasLocation ? (
            <Marker position={this.state.latlng}/>
        ) : null

        return (
            <Map
                center={[34,-108]}
                zoom={6}
                onClick={this.props.onClick}
            >
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                />
                {marker}
            </Map>
        );
    }
}

class App extends Component{
    constructor(props) {
        super(props);
        this.state = {
            'marker': {
                'hasLocation': false,
                'latlng': {
                    'lat': 34,
                    'lng': -108,
                }
            },
            'forecast': []
        };
    }

    handleClick = (e) => {
        this.setState({
            hasLocation: true,
            latlng: e.latlng
        });

        setWeatherForecast(e.latlng.lat, e.latlng.lng);
    }


    render() {
        return (
            <div className="App">
                <MapContainer
                    className="Map"
                    onClick={this.handleClick()}
                />
                <Forecast className="Forecast"/>
            </div>
        );
    }
}

export default App;

// get weather forecast
function setWeatherForecast(lat, long) {
    const data = null;

    const xhr = new XMLHttpRequest();

    xhr.addEventListener("readystatechange", function () {
        if (this.readyState === this.DONE) {
            return this.responseText;
        }
    });

    xhr.open("GET", "https://api.climacell.co/v3/weather/forecast/daily?lat=" + lat + "&lon=" + long + "&unit_system=us&start_time=now&fields=precipitation%2Cprecipitation_accumulation%2Ctemp%2Cwind_speed%2Cbaro_pressure%2Cvisibility%2Chumidity%2Cweather_code&apikey=9HaH9EKcMl4ANqi3eBna6kH58fybWmTu");
    xhr.send(data);
}

function f_to_c(t) {
    return (t - 32) * (5/9);
}

function c_to_f(t) {
    return (t * (9/5)) + 32;
}

// uses C degrees and percent
function temp_hum_to_dew(temp, humidity) {
    return (Math.pow((humidity/100), 0.125) * (112 + (0.9 * temp)))
        + (0.1 * temp)
        - 112;
}
