import './App.css';
import React, { Component } from 'react'
import { Map, TileLayer, Marker } from  "react-leaflet";

function Forecast(props) {
/*
  updateTable() {


        date.innerHTML = day['observation_time'].value;
        temp.innerHTML = (day['temp'][0]['min'].value + day['temp'][1]['max'].value) / 2;
        forecast.innerHTML = day['weather_code'].value;
    }
    */

    return (
      <div>
        <table id="forecastTable">
        <tr>
          <th>Day</th>
          <th>Temperature</th>
          <th>Forecast</th>
        </tr>
        </table>
      </div>
    );
}

function MapContainer (props) {
    const marker = props.marker.hasLocation ? (
        <Marker position={props.marker.latlng}/>
    ) : null

    return (
        <Map
            center={[34,-108]}
            zoom={6}
            onClick={props.onClick}
        >
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            />
            {marker}
        </Map>
    );
}

class App extends Component{
    constructor(props) {
        super(props);
        this.state = {
            'marker': {
                'hasLocation': false,
                'latlng': {
                    'lat': 34,
                    'lng': -108,
                }
            },
            'forecast': []
        };
    }

    handleClick = (e) => {
        this.setState({'marker': {
                hasLocation: true,
                latlng: e.latlng
            }
        });

        // get weather forecast
        fetch("https://api.climacell.co/v3/weather/forecast/daily?lat=" + e.latlng.lat + "&lon=" + e.latlng.lng + "&unit_system=us&start_time=now&fields=precipitation%2Cprecipitation_accumulation%2Ctemp%2Cwind_speed%2Cbaro_pressure%2Cvisibility%2Chumidity%2Cweather_code&apikey=9HaH9EKcMl4ANqi3eBna6kH58fybWmTu")
            .then(res => res.json())
            .then(
                (result) => {
                    this.setState({
                        'forecast': {
                            isLoaded: true,
                            items: result.items
                        }
                    });
                    return JSON.parse(this.responseText);
                })
        console.log(this.state)
    }


    render() {
        return (
            <div className="App">
                <MapContainer
                    className="Map"
                    onClick={e => this.handleClick(e)}
                    marker={this.state.marker}
                />
                <Forecast
                    className="Forecast"
                    forecast={this.state.forecast}
                />
            </div>
        );
    }
}

export default App;

function f_to_c(t) {
    return (t - 32) * (5/9);
}

function c_to_f(t) {
    return (t * (9/5)) + 32;
}

// uses C degrees and percent
function temp_hum_to_dew(temp, humidity) {
    return (Math.pow((humidity/100), 0.125) * (112 + (0.9 * temp)))
        + (0.1 * temp)
        - 112;
}
